import{_ as t,c as l,o as a,ae as e}from"./chunks/framework.CvJVpCFi.js";const u=JSON.parse('{"title":"国际主数据（MDM）咨询、实施公司清单","description":"国际主数据（MDM）咨询、实施公司清单","frontmatter":{"head":[["meta",{"name":"description","content":"国际主数据（MDM）咨询、实施公司清单"}],["meta",{"name":"keywords","content":"主数据,MDM,埃森哲,德勤,普华永道,凯捷,Informatica,SAP,Oracle,Stibo,Semarchy,Profisee,IBM,TCS,Wipro,HCLTech,英诺森,汉得,华为,咨询,实施,数据治理,集团主数据"}]]},"headers":[],"relativePath":"zh-CN/getting-started/international-consulting.md","filePath":"zh-CN/getting-started/international-consulting.md"}'),i={name:"zh-CN/getting-started/international-consulting.md"};function o(n,r,s,h,c,p){return a(),l("div",null,[...r[0]||(r[0]=[e('<h1 id="国际主数据-mdm-咨询、实施公司清单" tabindex="-1">国际主数据（MDM）咨询、实施公司清单 <a class="header-anchor" href="#国际主数据-mdm-咨询、实施公司清单" aria-label="Permalink to &quot;国际主数据（MDM）咨询、实施公司清单&quot;">​</a></h1><p>本文档整理 <strong>国际市场常用的主数据管理（MDM, Master Data Management）相关厂商</strong>，涵盖 <strong>咨询公司、平台厂商、实施服务商</strong>。清单以国际主流选型视角为主，同时 <strong>包含在国际项目中常见的中国公司</strong>。</p><blockquote><p>适用对象：</p><ul><li>跨国企业 / 出海企业主数据选型</li><li>全球主数据治理（Global MDM）项目</li><li>对标国际厂商能力、做方案或竞品分析</li></ul></blockquote><hr><h2 id="一、厂商分类说明" tabindex="-1">一、厂商分类说明 <a class="header-anchor" href="#一、厂商分类说明" aria-label="Permalink to &quot;一、厂商分类说明&quot;">​</a></h2><p>国际主数据项目通常按以下角色组合推进：</p><ul><li><p><strong>A 类：国际咨询公司（战略 + 治理）</strong> 提供全球主数据战略、治理模型、组织与流程设计。</p></li><li><p><strong>B 类：国际 MDM 平台厂商</strong> 提供成熟、国际化的主数据管理软件产品，是项目技术核心。</p></li><li><p><strong>C 类：国际实施服务商 / SI</strong> 负责平台实施、系统集成、本地化交付。</p></li></ul><blockquote><p>常见组合：<strong>A 类咨询 + B 类平台 + C 类实施（或同一厂商多角色）</strong>。</p></blockquote><hr><h2 id="a-国际咨询公司-战略-治理" tabindex="-1">A) 国际咨询公司（战略 + 治理） <a class="header-anchor" href="#a-国际咨询公司-战略-治理" aria-label="Permalink to &quot;A) 国际咨询公司（战略 + 治理）&quot;">​</a></h2><p><strong>定位</strong>：全球主数据战略、数据治理方法论、跨区域治理模型设计</p><h3 id="_1-accenture-埃森哲" tabindex="-1">1. Accenture（埃森哲） <a class="header-anchor" href="#_1-accenture-埃森哲" aria-label="Permalink to &quot;1. Accenture（埃森哲）&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.accenture.com" target="_blank" rel="noreferrer">https://www.accenture.com</a></p></li><li><p><strong>公司定位</strong>：全球最大数字化与管理咨询公司之一</p></li><li><p><strong>主数据能力</strong>：</p><ul><li>Global MDM 战略与治理模型</li><li>数据治理组织与流程设计</li><li>MDM 与 ERP / Data Platform 深度结合</li></ul></li><li><p><strong>典型场景</strong>：</p><ul><li>跨国集团主数据统一</li><li>大型复杂治理项目</li></ul></li></ul><hr><h3 id="_2-deloitte-德勤" tabindex="-1">2. Deloitte（德勤） <a class="header-anchor" href="#_2-deloitte-德勤" aria-label="Permalink to &quot;2. Deloitte（德勤）&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.deloitte.com" target="_blank" rel="noreferrer">https://www.deloitte.com</a></p></li><li><p><strong>公司定位</strong>：国际四大之一</p></li><li><p><strong>主数据能力</strong>：</p><ul><li>数据治理与主数据咨询</li><li>主数据管理成熟度评估</li><li>与 SAP / Oracle / Informatica 深度合作</li></ul></li><li><p><strong>典型场景</strong>：</p><ul><li>集团治理、合规驱动的 MDM 项目</li></ul></li></ul><hr><h3 id="_3-pwc-普华永道" tabindex="-1">3. PwC（普华永道） <a class="header-anchor" href="#_3-pwc-普华永道" aria-label="Permalink to &quot;3. PwC（普华永道）&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.pwc.com" target="_blank" rel="noreferrer">https://www.pwc.com</a></p></li><li><p><strong>公司定位</strong>：国际四大之一</p></li><li><p><strong>主数据能力</strong>：</p><ul><li>数据治理与主数据战略</li><li>主数据与财务、风险、合规结合</li></ul></li><li><p><strong>典型场景</strong>：</p><ul><li>财务主数据、集团治理</li></ul></li></ul><hr><h3 id="_4-capgemini-凯捷" tabindex="-1">4. Capgemini（凯捷） <a class="header-anchor" href="#_4-capgemini-凯捷" aria-label="Permalink to &quot;4. Capgemini（凯捷）&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.capgemini.com" target="_blank" rel="noreferrer">https://www.capgemini.com</a></p></li><li><p><strong>公司定位</strong>：欧洲大型 IT 咨询与系统集成商</p></li><li><p><strong>主数据能力</strong>：</p><ul><li>MDM 咨询 + 实施一体化</li><li>强制造业与零售业经验</li></ul></li></ul><hr><h2 id="b-国际主数据-mdm-平台厂商" tabindex="-1">B) 国际主数据（MDM）平台厂商 <a class="header-anchor" href="#b-国际主数据-mdm-平台厂商" aria-label="Permalink to &quot;B) 国际主数据（MDM）平台厂商&quot;">​</a></h2><p><strong>定位</strong>：提供成熟、国际化的主数据管理产品</p><h3 id="_1-informatica" tabindex="-1">1. Informatica <a class="header-anchor" href="#_1-informatica" aria-label="Permalink to &quot;1. Informatica&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.informatica.com" target="_blank" rel="noreferrer">https://www.informatica.com</a></p></li><li><p><strong>产品</strong>：Informatica MDM / IDMC</p></li><li><p><strong>特点</strong>：</p><ul><li>全球市场占有率最高之一</li><li>功能全面、生态成熟</li></ul></li><li><p><strong>适用场景</strong>：</p><ul><li>全球统一 MDM 平台</li></ul></li></ul><hr><h3 id="_2-sap" tabindex="-1">2. SAP <a class="header-anchor" href="#_2-sap" aria-label="Permalink to &quot;2. SAP&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.sap.com" target="_blank" rel="noreferrer">https://www.sap.com</a></p></li><li><p><strong>产品</strong>：SAP MDG（Master Data Governance）</p></li><li><p><strong>特点</strong>：</p><ul><li>ERP 强绑定</li><li>制造业、集团客户广泛</li></ul></li></ul><hr><h3 id="_3-oracle" tabindex="-1">3. Oracle <a class="header-anchor" href="#_3-oracle" aria-label="Permalink to &quot;3. Oracle&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.oracle.com" target="_blank" rel="noreferrer">https://www.oracle.com</a></p></li><li><p><strong>产品</strong>：Oracle EDM / CDM / MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>强数据库与应用生态</li></ul></li></ul><hr><h3 id="_4-stibo-systems" tabindex="-1">4. Stibo Systems <a class="header-anchor" href="#_4-stibo-systems" aria-label="Permalink to &quot;4. Stibo Systems&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.stibosystems.com" target="_blank" rel="noreferrer">https://www.stibosystems.com</a></p></li><li><p><strong>产品</strong>：STEP MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>零售、消费品、PIM 强项</li></ul></li></ul><hr><h3 id="_5-semarchy" tabindex="-1">5. Semarchy <a class="header-anchor" href="#_5-semarchy" aria-label="Permalink to &quot;5. Semarchy&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.semarchy.com" target="_blank" rel="noreferrer">https://www.semarchy.com</a></p></li><li><p><strong>产品</strong>：xDM</p></li><li><p><strong>特点</strong>：</p><ul><li>轻量、模型驱动</li><li>中大型企业常用</li></ul></li></ul><hr><h3 id="_6-profisee" tabindex="-1">6. Profisee <a class="header-anchor" href="#_6-profisee" aria-label="Permalink to &quot;6. Profisee&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.profisee.com" target="_blank" rel="noreferrer">https://www.profisee.com</a></p></li><li><p><strong>产品</strong>：Profisee MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>Microsoft Azure 生态友好</li></ul></li></ul><hr><h2 id="c-国际实施服务商-si-含中国公司" tabindex="-1">C) 国际实施服务商 / SI（含中国公司） <a class="header-anchor" href="#c-国际实施服务商-si-含中国公司" aria-label="Permalink to &quot;C) 国际实施服务商 / SI（含中国公司）&quot;">​</a></h2><p><strong>定位</strong>：平台实施、系统集成、本地与全球交付</p><h3 id="_1-ibm" tabindex="-1">1. IBM <a class="header-anchor" href="#_1-ibm" aria-label="Permalink to &quot;1. IBM&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.ibm.com" target="_blank" rel="noreferrer">https://www.ibm.com</a></p></li><li><p><strong>主数据能力</strong>：</p><ul><li>IBM MDM</li><li>数据治理与系统集成</li></ul></li></ul><hr><h3 id="_2-tcs-tata-consultancy-services" tabindex="-1">2. TCS（Tata Consultancy Services） <a class="header-anchor" href="#_2-tcs-tata-consultancy-services" aria-label="Permalink to &quot;2. TCS（Tata Consultancy Services）&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.tcs.com" target="_blank" rel="noreferrer">https://www.tcs.com</a></p></li><li><p><strong>定位</strong>：全球大型 IT 服务商</p></li><li><p><strong>主数据能力</strong>：</p><ul><li>大规模 MDM 实施</li></ul></li></ul><hr><h3 id="_3-wipro" tabindex="-1">3. Wipro <a class="header-anchor" href="#_3-wipro" aria-label="Permalink to &quot;3. Wipro&quot;">​</a></h3><ul><li><strong>官网</strong>：<a href="https://www.wipro.com" target="_blank" rel="noreferrer">https://www.wipro.com</a></li><li><strong>定位</strong>：全球 IT 服务商</li></ul><hr><h3 id="_4-hcltech" tabindex="-1">4. HCLTech <a class="header-anchor" href="#_4-hcltech" aria-label="Permalink to &quot;4. HCLTech&quot;">​</a></h3><ul><li><strong>官网</strong>：<a href="https://www.hcltech.com" target="_blank" rel="noreferrer">https://www.hcltech.com</a></li><li><strong>定位</strong>：全球 IT 服务商</li></ul><hr><h3 id="_5-英诺森-inossem-indata-中国" tabindex="-1">5. 英诺森（Inossem / InData，中国） <a class="header-anchor" href="#_5-英诺森-inossem-indata-中国" aria-label="Permalink to &quot;5. 英诺森（Inossem / InData，中国）&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.inossem.com" target="_blank" rel="noreferrer">https://www.inossem.com</a></p></li><li><p><strong>定位</strong>：国际实施服务商（中国 + 北美）</p></li><li><p><strong>主数据能力</strong>：</p><ul><li>工业主数据 MDM（物料 / 设备 / BOM）</li><li>跨国项目交付能力</li></ul></li></ul><hr><h3 id="_6-汉得-hand-中国" tabindex="-1">6. 汉得（HAND，中国） <a class="header-anchor" href="#_6-汉得-hand-中国" aria-label="Permalink to &quot;6. 汉得（HAND，中国）&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.hand-china.com" target="_blank" rel="noreferrer">https://www.hand-china.com</a></p></li><li><p><strong>国际能力</strong>：</p><ul><li>SAP / Oracle 全球交付</li><li>中国企业出海主数据项目</li></ul></li></ul><hr><h3 id="_7-华为-华为云-中国" tabindex="-1">7. 华为 / 华为云（中国） <a class="header-anchor" href="#_7-华为-华为云-中国" aria-label="Permalink to &quot;7. 华为 / 华为云（中国）&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.huawei.com" target="_blank" rel="noreferrer">https://www.huawei.com</a></p></li><li><p><strong>华为云</strong>：<a href="https://www.huaweicloud.com" target="_blank" rel="noreferrer">https://www.huaweicloud.com</a></p></li><li><p><strong>国际能力</strong>：</p><ul><li>数据治理平台</li><li>海外云与数据平台项目</li></ul></li></ul><hr><h2 id="四、简要选型建议" tabindex="-1">四、简要选型建议 <a class="header-anchor" href="#四、简要选型建议" aria-label="Permalink to &quot;四、简要选型建议&quot;">​</a></h2><ul><li><strong>全球治理战略优先</strong>：Accenture / Deloitte</li><li><strong>成熟 MDM 平台选型</strong>：Informatica / SAP / Stibo</li><li><strong>国际交付 &amp; 中国经验结合</strong>：英诺森 / 汉得 / 华为</li><li><strong>零售 / 消费品</strong>：Stibo Systems</li><li><strong>Azure 技术栈</strong>：Profisee</li></ul><hr><h2 id="五、备注" tabindex="-1">五、备注 <a class="header-anchor" href="#五、备注" aria-label="Permalink to &quot;五、备注&quot;">​</a></h2><ul><li>本清单聚焦国际主流与常见厂商，非穷尽列表。</li><li>实际选型建议结合行业、IT 架构、数据复杂度进行 PoC 验证。</li></ul>',71)])])}const m=t(i,[["render",o]]);export{u as __pageData,m as default};
