import{_ as t,o as a,c as e,ae as l}from"./chunks/framework.f9sx20OT.js";const d=JSON.parse('{"title":"主数据管理系统（MDM）系统清单","description":"主数据管理系统（MDM）系统清单","frontmatter":{"head":[["meta",{"name":"description","content":"主数据管理系统（MDM）系统清单"}],["meta",{"name":"keywords","content":"主数据,MDM,Informatica,SAP,Oracle,Stibo,Semarchy,Profisee,IBM,TCS,Wipro,HCLTech,英诺森,汉得,华为,咨询,实施,数据治理,集团主数据"}]]},"headers":[],"relativePath":"zh-CN/getting-started/master-data-system.md","filePath":"zh-CN/getting-started/master-data-system.md","lastUpdated":1769584805000}'),o={name:"zh-CN/getting-started/master-data-system.md"};function i(n,r,s,h,m,g){return a(),e("div",null,[...r[0]||(r[0]=[l('<h1 id="主数据管理系统-mdm-系统清单" tabindex="-1">主数据管理系统（MDM）系统清单 <a class="header-anchor" href="#主数据管理系统-mdm-系统清单" aria-label="Permalink to &quot;主数据管理系统（MDM）系统清单&quot;">​</a></h1><blockquote><p>本文档整理 <strong>主数据管理系统（MDM, Master Data Management）</strong> 的主流产品，覆盖：</p><ul><li>🌍 国际厂商 / 🇨🇳 中国厂商</li><li>💰 商业收费 / 🔓 开源（含社区版）</li><li>🧩 多域 MDM / 客户、产品、供应商等专用 MDM</li></ul></blockquote><p>适用于 <strong>主数据平台选型、技术对标、方案设计、RFP 编制</strong> 等场景。</p><h2 id="一、国际主流商业-mdm-系统-收费" tabindex="-1">一、国际主流商业 MDM 系统（收费） <a class="header-anchor" href="#一、国际主流商业-mdm-系统-收费" aria-label="Permalink to &quot;一、国际主流商业 MDM 系统（收费）&quot;">​</a></h2><h3 id="_1-informatica-mdm" tabindex="-1">1. Informatica MDM <a class="header-anchor" href="#_1-informatica-mdm" aria-label="Permalink to &quot;1. Informatica MDM&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.informatica.com" target="_blank" rel="noreferrer">https://www.informatica.com</a></p></li><li><p><strong>类型</strong>：多域 MDM（Multi-domain MDM）</p></li><li><p><strong>特点</strong>：</p><ul><li>全球市场占有率高</li><li>数据治理、质量、集成能力完整</li></ul></li><li><p><strong>适用场景</strong>：大型跨国企业、集团型 MDM</p></li></ul><h3 id="_2-sap-master-data-governance-mdg" tabindex="-1">2. SAP Master Data Governance（MDG） <a class="header-anchor" href="#_2-sap-master-data-governance-mdg" aria-label="Permalink to &quot;2. SAP Master Data Governance（MDG）&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.sap.com" target="_blank" rel="noreferrer">https://www.sap.com</a></p></li><li><p><strong>类型</strong>：ERP 内嵌型 MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>与 SAP S/4HANA 深度绑定</li><li>制造业、集团客户广泛</li></ul></li></ul><h3 id="_3-oracle-master-data-management" tabindex="-1">3. Oracle Master Data Management <a class="header-anchor" href="#_3-oracle-master-data-management" aria-label="Permalink to &quot;3. Oracle Master Data Management&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.oracle.com" target="_blank" rel="noreferrer">https://www.oracle.com</a></p></li><li><p><strong>类型</strong>：企业级多域 MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>Oracle 应用与数据库生态强</li></ul></li></ul><h3 id="_4-stibo-systems-step" tabindex="-1">4. Stibo Systems STEP <a class="header-anchor" href="#_4-stibo-systems-step" aria-label="Permalink to &quot;4. Stibo Systems STEP&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.stibosystems.com" target="_blank" rel="noreferrer">https://www.stibosystems.com</a></p></li><li><p><strong>类型</strong>：PIM + MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>零售、消费品、电商强项</li></ul></li></ul><h3 id="_5-reltio-connected-data-platform" tabindex="-1">5. Reltio Connected Data Platform <a class="header-anchor" href="#_5-reltio-connected-data-platform" aria-label="Permalink to &quot;5. Reltio Connected Data Platform&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.reltio.com" target="_blank" rel="noreferrer">https://www.reltio.com</a></p></li><li><p><strong>类型</strong>：云原生 MDM / Customer 360</p></li><li><p><strong>特点</strong>：</p><ul><li>客户主数据、关系型数据建模</li></ul></li></ul><h3 id="_6-profisee-mdm" tabindex="-1">6. Profisee MDM <a class="header-anchor" href="#_6-profisee-mdm" aria-label="Permalink to &quot;6. Profisee MDM&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.profisee.com" target="_blank" rel="noreferrer">https://www.profisee.com</a></p></li><li><p><strong>类型</strong>：多域 MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>Azure / Microsoft 生态友好</li></ul></li></ul><h3 id="_7-semarchy-xdm" tabindex="-1">7. Semarchy xDM <a class="header-anchor" href="#_7-semarchy-xdm" aria-label="Permalink to &quot;7. Semarchy xDM&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.semarchy.com" target="_blank" rel="noreferrer">https://www.semarchy.com</a></p></li><li><p><strong>类型</strong>：模型驱动 MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>轻量、快速实施</li></ul></li></ul><h3 id="_8-talend-mdm" tabindex="-1">8. Talend MDM <a class="header-anchor" href="#_8-talend-mdm" aria-label="Permalink to &quot;8. Talend MDM&quot;">​</a></h3><ul><li><strong>官网</strong>：<a href="https://www.talend.com" target="_blank" rel="noreferrer">https://www.talend.com</a></li><li><strong>类型</strong>：MDM + 数据集成</li></ul><h3 id="_9-ibm-infosphere-mdm" tabindex="-1">9. IBM InfoSphere MDM <a class="header-anchor" href="#_9-ibm-infosphere-mdm" aria-label="Permalink to &quot;9. IBM InfoSphere MDM&quot;">​</a></h3><ul><li><strong>官网</strong>：<a href="https://www.ibm.com" target="_blank" rel="noreferrer">https://www.ibm.com</a></li><li><strong>类型</strong>：企业级 MDM</li></ul><h2 id="二、中国商业主数据管理系统" tabindex="-1">二、中国商业主数据管理系统 <a class="header-anchor" href="#二、中国商业主数据管理系统" aria-label="Permalink to &quot;二、中国商业主数据管理系统&quot;">​</a></h2><h3 id="_1-用友-主数据中台-mdm" tabindex="-1">1. 用友 主数据中台 / MDM <a class="header-anchor" href="#_1-用友-主数据中台-mdm" aria-label="Permalink to &quot;1. 用友 主数据中台 / MDM&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.yonyou.com" target="_blank" rel="noreferrer">https://www.yonyou.com</a></p></li><li><p><strong>类型</strong>：集团级 MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>多组织、多法人治理</li><li>与财务、供应链深度集成</li></ul></li></ul><h3 id="_2-金蝶-主数据管理平台" tabindex="-1">2. 金蝶 主数据管理平台 <a class="header-anchor" href="#_2-金蝶-主数据管理平台" aria-label="Permalink to &quot;2. 金蝶 主数据管理平台&quot;">​</a></h3><ul><li><strong>官网</strong>：<a href="https://www.kingdee.com" target="_blank" rel="noreferrer">https://www.kingdee.com</a></li><li><strong>类型</strong>：ERP 绑定型 MDM</li></ul><h3 id="_3-普元-mdm-primeton" tabindex="-1">3. 普元 MDM（Primeton） <a class="header-anchor" href="#_3-普元-mdm-primeton" aria-label="Permalink to &quot;3. 普元 MDM（Primeton）&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.primeton.com" target="_blank" rel="noreferrer">https://www.primeton.com</a></p></li><li><p><strong>类型</strong>：国产多域 MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>国产化替代</li><li>政企客户</li></ul></li></ul><h3 id="_4-英诺森-indata-mdm" tabindex="-1">4. 英诺森 InData MDM <a class="header-anchor" href="#_4-英诺森-indata-mdm" aria-label="Permalink to &quot;4. 英诺森 InData MDM&quot;">​</a></h3><ul><li><p><strong>官网</strong>：<a href="https://www.inossem.com" target="_blank" rel="noreferrer">https://www.inossem.com</a></p></li><li><p><strong>类型</strong>：工业主数据 MDM</p></li><li><p><strong>特点</strong>：</p><ul><li>物料 / 设备 / BOM 主数据</li></ul></li></ul><h3 id="_5-汉得-主数据平台-hand-mdm" tabindex="-1">5. 汉得 主数据平台（HAND MDM） <a class="header-anchor" href="#_5-汉得-主数据平台-hand-mdm" aria-label="Permalink to &quot;5. 汉得 主数据平台（HAND MDM）&quot;">​</a></h3><ul><li><strong>官网</strong>：<a href="https://www.hand-china.com" target="_blank" rel="noreferrer">https://www.hand-china.com</a></li><li><strong>类型</strong>：实施型 MDM 平台</li></ul><h3 id="_6-三维天地-主数据管理系统" tabindex="-1">6. 三维天地 主数据管理系统 <a class="header-anchor" href="#_6-三维天地-主数据管理系统" aria-label="Permalink to &quot;6. 三维天地 主数据管理系统&quot;">​</a></h3><ul><li><strong>官网</strong>：<a href="https://www.sunwayworld.com" target="_blank" rel="noreferrer">https://www.sunwayworld.com</a></li><li><strong>类型</strong>：国产 MDM</li></ul><h2 id="三、开源-社区版主数据系统" tabindex="-1">三、开源 / 社区版主数据系统 <a class="header-anchor" href="#三、开源-社区版主数据系统" aria-label="Permalink to &quot;三、开源 / 社区版主数据系统&quot;">​</a></h2><blockquote><p>⚠️ 多数开源 MDM 偏产品数据（PIM）或轻量 MDM，企业级使用需二次开发。</p></blockquote><h3 id="_1-pimcore" tabindex="-1">1. Pimcore <a class="header-anchor" href="#_1-pimcore" aria-label="Permalink to &quot;1. Pimcore&quot;">​</a></h3><ul><li><strong>官网</strong>：<a href="https://pimcore.com" target="_blank" rel="noreferrer">https://pimcore.com</a></li><li><strong>授权</strong>：GPL（社区版）+ 企业版收费</li><li><strong>类型</strong>：PIM / MDM / DAM</li></ul><h3 id="_2-atrocore" tabindex="-1">2. AtroCore <a class="header-anchor" href="#_2-atrocore" aria-label="Permalink to &quot;2. AtroCore&quot;">​</a></h3><ul><li><strong>官网</strong>：<a href="https://www.atrocore.com" target="_blank" rel="noreferrer">https://www.atrocore.com</a></li><li><strong>授权</strong>：开源 + SaaS</li><li><strong>类型</strong>：PIM + MDM</li></ul><h3 id="_3-openmdm" tabindex="-1">3. OpenMDM <a class="header-anchor" href="#_3-openmdm" aria-label="Permalink to &quot;3. OpenMDM&quot;">​</a></h3><ul><li><strong>官网</strong>：<a href="https://github.com/openmdm" target="_blank" rel="noreferrer">https://github.com/openmdm</a></li><li><strong>授权</strong>：开源</li><li><strong>类型</strong>：基础 MDM 框架</li></ul><h3 id="_4-hivemdm" tabindex="-1">4. HiveMDM <a class="header-anchor" href="#_4-hivemdm" aria-label="Permalink to &quot;4. HiveMDM&quot;">​</a></h3><ul><li><strong>授权</strong>：开源</li><li><strong>类型</strong>：技术型 MDM 方案</li></ul><h3 id="_5-piemdm" tabindex="-1">5. PieMDM <a class="header-anchor" href="#_5-piemdm" aria-label="Permalink to &quot;5. PieMDM&quot;">​</a></h3><ul><li><p><strong>官网 / 项目地址</strong>：<a href="https://github.com/pieteams/piemdm" target="_blank" rel="noreferrer">https://github.com/pieteams/piemdm</a></p></li><li><p><strong>项目网站</strong>：<a href="https://pieteams.github.io/piemdm/" target="_blank" rel="noreferrer">https://pieteams.github.io/piemdm/</a></p></li><li><p><strong>授权</strong>：MIT 开源许可</p></li><li><p><strong>类型</strong>：开源企业级 MDM 系统</p></li><li><p><strong>技术栈</strong>：Go 后端 + Vue 前端</p></li><li><p><strong>主要功能</strong>：</p><ul><li>主数据建模与数据管理</li><li>数据治理与访问控制</li><li>工作流 / 审批管理</li><li>系统集成 &amp; API 支持</li></ul></li><li><p><strong>适用场景</strong>：</p><ul><li>适合尝试性、PoC 或希望基于源码扩展的主数据项目</li><li>社区实践、自主研发型企业</li></ul></li></ul><h2 id="四、按维度快速索引" tabindex="-1">四、按维度快速索引 <a class="header-anchor" href="#四、按维度快速索引" aria-label="Permalink to &quot;四、按维度快速索引&quot;">​</a></h2><h3 id="商业-vs-开源" tabindex="-1">商业 vs 开源 <a class="header-anchor" href="#商业-vs-开源" aria-label="Permalink to &quot;商业 vs 开源&quot;">​</a></h3><ul><li><strong>商业</strong>：Informatica / SAP / Oracle / Stibo / 用友 / 普元</li><li><strong>开源</strong>：Pimcore / AtroCore / OpenMDM / PieMDM</li></ul><h3 id="国际-vs-中国" tabindex="-1">国际 vs 中国 <a class="header-anchor" href="#国际-vs-中国" aria-label="Permalink to &quot;国际 vs 中国&quot;">​</a></h3><ul><li><strong>国际</strong>：Informatica / SAP / Oracle / Stibo / Reltio</li><li><strong>中国</strong>：用友 / 金蝶 / 普元 / 英诺森 / 汉得 / PieMDM</li></ul><h2 id="五、选型建议-简要" tabindex="-1">五、选型建议（简要） <a class="header-anchor" href="#五、选型建议-简要" aria-label="Permalink to &quot;五、选型建议（简要）&quot;">​</a></h2><ul><li><strong>全球集团 / 复杂治理</strong>：Informatica / SAP MDG</li><li><strong>制造业主数据</strong>：SAP MDG / 英诺森</li><li><strong>国产化要求</strong>：用友 / 普元 / 金蝶 / PieMDM</li><li><strong>低成本探索 / 原型验证</strong>：Pimcore / AtroCore / PieMDM</li></ul>',54)])])}const c=t(o,[["render",i]]);export{d as __pageData,c as default};
