import{_ as t,o as a,c as e,ae as l}from"./chunks/framework.f9sx20OT.js";const c=JSON.parse('{"title":"主數據管理系統（MDM）系統清單","description":"","frontmatter":{},"headers":[],"relativePath":"zh-TW/getting-started/master-data-system.md","filePath":"zh-TW/getting-started/master-data-system.md","lastUpdated":1769511796000}'),o={name:"zh-TW/getting-started/master-data-system.md"};function i(n,r,s,h,m,g){return a(),e("div",null,[...r[0]||(r[0]=[l('<h1 id="主數據管理系統-mdm-系統清單" tabindex="-1">主數據管理系統（MDM）系統清單 <a class="header-anchor" href="#主數據管理系統-mdm-系統清單" aria-label="Permalink to &quot;主數據管理系統（MDM）系統清單&quot;">​</a></h1><blockquote><p>本文檔整理 <strong>主數據管理系統（MDM, Master Data Management）</strong> 的主流產品，覆蓋：</p><ul><li>🌍 國際廠商 / 🇨🇳 中國廠商</li><li>💰 商業收費 / 🔓 開源（含社區版）</li><li>🧩 多域 MDM / 客戶、產品、供應商等專用 MDM</li></ul></blockquote><p>適用於 <strong>主數據平臺選型、技術對標、方案設計、RFP 編製</strong> 等場景。</p><h2 id="一、國際主流商業-mdm-系統-收費" tabindex="-1">一、國際主流商業 MDM 系統（收費） <a class="header-anchor" href="#一、國際主流商業-mdm-系統-收費" aria-label="Permalink to &quot;一、國際主流商業 MDM 系統（收費）&quot;">​</a></h2><h3 id="_1-informatica-mdm" tabindex="-1">1. Informatica MDM <a class="header-anchor" href="#_1-informatica-mdm" aria-label="Permalink to &quot;1. Informatica MDM&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.informatica.com" target="_blank" rel="noreferrer">https://www.informatica.com</a></li><li><strong>類型</strong>：多域 MDM（Multi-domain MDM）</li><li><strong>特點</strong>： <ul><li>全球市場佔有率高</li><li>數據治理、質量、集成能力完整</li></ul></li><li><strong>適用場景</strong>：大型跨國企業、集團型 MDM</li></ul><h3 id="_2-sap-master-data-governance-mdg" tabindex="-1">2. SAP Master Data Governance（MDG） <a class="header-anchor" href="#_2-sap-master-data-governance-mdg" aria-label="Permalink to &quot;2. SAP Master Data Governance（MDG）&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.sap.com" target="_blank" rel="noreferrer">https://www.sap.com</a></li><li><strong>類型</strong>：ERP 內嵌型 MDM</li><li><strong>特點</strong>： <ul><li>與 SAP S/4HANA 深度綁定</li><li>製造業、集團客戶廣泛</li></ul></li></ul><h3 id="_3-oracle-master-data-management" tabindex="-1">3. Oracle Master Data Management <a class="header-anchor" href="#_3-oracle-master-data-management" aria-label="Permalink to &quot;3. Oracle Master Data Management&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.oracle.com" target="_blank" rel="noreferrer">https://www.oracle.com</a></li><li><strong>類型</strong>：企業級多域 MDM</li><li><strong>特點</strong>： <ul><li>Oracle 應用與數據庫生態強</li></ul></li></ul><h3 id="_4-stibo-systems-step" tabindex="-1">4. Stibo Systems STEP <a class="header-anchor" href="#_4-stibo-systems-step" aria-label="Permalink to &quot;4. Stibo Systems STEP&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.stibosystems.com" target="_blank" rel="noreferrer">https://www.stibosystems.com</a></li><li><strong>類型</strong>：PIM + MDM</li><li><strong>特點</strong>： <ul><li>零售、消費品、電商強項</li></ul></li></ul><h3 id="_5-reltio-connected-data-platform" tabindex="-1">5. Reltio Connected Data Platform <a class="header-anchor" href="#_5-reltio-connected-data-platform" aria-label="Permalink to &quot;5. Reltio Connected Data Platform&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.reltio.com" target="_blank" rel="noreferrer">https://www.reltio.com</a></li><li><strong>類型</strong>：雲原生 MDM / Customer 360</li><li><strong>特點</strong>： <ul><li>客戶主數據、關係型數據建模</li></ul></li></ul><h3 id="_6-profisee-mdm" tabindex="-1">6. Profisee MDM <a class="header-anchor" href="#_6-profisee-mdm" aria-label="Permalink to &quot;6. Profisee MDM&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.profisee.com" target="_blank" rel="noreferrer">https://www.profisee.com</a></li><li><strong>類型</strong>：多域 MDM</li><li><strong>特點</strong>： <ul><li>Azure / Microsoft 生態友好</li></ul></li></ul><h3 id="_7-semarchy-xdm" tabindex="-1">7. Semarchy xDM <a class="header-anchor" href="#_7-semarchy-xdm" aria-label="Permalink to &quot;7. Semarchy xDM&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.semarchy.com" target="_blank" rel="noreferrer">https://www.semarchy.com</a></li><li><strong>類型</strong>：模型驅動 MDM</li><li><strong>特點</strong>： <ul><li>輕量、快速實施</li></ul></li></ul><h3 id="_8-talend-mdm" tabindex="-1">8. Talend MDM <a class="header-anchor" href="#_8-talend-mdm" aria-label="Permalink to &quot;8. Talend MDM&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.talend.com" target="_blank" rel="noreferrer">https://www.talend.com</a></li><li><strong>類型</strong>：MDM + 數據集成</li></ul><h3 id="_9-ibm-infosphere-mdm" tabindex="-1">9. IBM InfoSphere MDM <a class="header-anchor" href="#_9-ibm-infosphere-mdm" aria-label="Permalink to &quot;9. IBM InfoSphere MDM&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.ibm.com" target="_blank" rel="noreferrer">https://www.ibm.com</a></li><li><strong>類型</strong>：企業級 MDM</li></ul><h2 id="二、中國商業主數據管理系統" tabindex="-1">二、中國商業主數據管理系統 <a class="header-anchor" href="#二、中國商業主數據管理系統" aria-label="Permalink to &quot;二、中國商業主數據管理系統&quot;">​</a></h2><h3 id="_1-用友-主數據中臺-mdm" tabindex="-1">1. 用友 主數據中臺 / MDM <a class="header-anchor" href="#_1-用友-主數據中臺-mdm" aria-label="Permalink to &quot;1. 用友 主數據中臺 / MDM&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.yonyou.com" target="_blank" rel="noreferrer">https://www.yonyou.com</a></li><li><strong>類型</strong>：集團級 MDM</li><li><strong>特點</strong>： <ul><li>多組織、多法人治理</li><li>與財務、供應鏈深度集成</li></ul></li></ul><h3 id="_2-金蝶-主數據管理平臺" tabindex="-1">2. 金蝶 主數據管理平臺 <a class="header-anchor" href="#_2-金蝶-主數據管理平臺" aria-label="Permalink to &quot;2. 金蝶 主數據管理平臺&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.kingdee.com" target="_blank" rel="noreferrer">https://www.kingdee.com</a></li><li><strong>類型</strong>：ERP 綁定型 MDM</li></ul><h3 id="_3-普元-mdm-primeton" tabindex="-1">3. 普元 MDM（Primeton） <a class="header-anchor" href="#_3-普元-mdm-primeton" aria-label="Permalink to &quot;3. 普元 MDM（Primeton）&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.primeton.com" target="_blank" rel="noreferrer">https://www.primeton.com</a></li><li><strong>類型</strong>：國產多域 MDM</li><li><strong>特點</strong>： <ul><li>國產化替代</li><li>政企客戶</li></ul></li></ul><h3 id="_4-英諾森-indata-mdm" tabindex="-1">4. 英諾森 InData MDM <a class="header-anchor" href="#_4-英諾森-indata-mdm" aria-label="Permalink to &quot;4. 英諾森 InData MDM&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.inossem.com" target="_blank" rel="noreferrer">https://www.inossem.com</a></li><li><strong>類型</strong>：工業主數據 MDM</li><li><strong>特點</strong>： <ul><li>物料 / 設備 / BOM 主數據</li></ul></li></ul><h3 id="_5-漢得-主數據平臺-hand-mdm" tabindex="-1">5. 漢得 主數據平臺（HAND MDM） <a class="header-anchor" href="#_5-漢得-主數據平臺-hand-mdm" aria-label="Permalink to &quot;5. 漢得 主數據平臺（HAND MDM）&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.hand-china.com" target="_blank" rel="noreferrer">https://www.hand-china.com</a></li><li><strong>類型</strong>：實施型 MDM 平臺</li></ul><h3 id="_6-三維天地-主數據管理系統" tabindex="-1">6. 三維天地 主數據管理系統 <a class="header-anchor" href="#_6-三維天地-主數據管理系統" aria-label="Permalink to &quot;6. 三維天地 主數據管理系統&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.sunwayworld.com" target="_blank" rel="noreferrer">https://www.sunwayworld.com</a></li><li><strong>類型</strong>：國產 MDM</li></ul><h2 id="三、開源-社區版主數據系統" tabindex="-1">三、開源 / 社區版主數據系統 <a class="header-anchor" href="#三、開源-社區版主數據系統" aria-label="Permalink to &quot;三、開源 / 社區版主數據系統&quot;">​</a></h2><blockquote><p>⚠️ 多數開源 MDM 偏產品數據（PIM）或輕量 MDM，企業級使用需二次開發。</p></blockquote><h3 id="_1-pimcore" tabindex="-1">1. Pimcore <a class="header-anchor" href="#_1-pimcore" aria-label="Permalink to &quot;1. Pimcore&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://pimcore.com" target="_blank" rel="noreferrer">https://pimcore.com</a></li><li><strong>授權</strong>：GPL（社區版）+ 企業版收費</li><li><strong>類型</strong>：PIM / MDM / DAM</li></ul><h3 id="_2-atrocore" tabindex="-1">2. AtroCore <a class="header-anchor" href="#_2-atrocore" aria-label="Permalink to &quot;2. AtroCore&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://www.atrocore.com" target="_blank" rel="noreferrer">https://www.atrocore.com</a></li><li><strong>授權</strong>：開源 + SaaS</li><li><strong>類型</strong>：PIM + MDM</li></ul><h3 id="_3-openmdm" tabindex="-1">3. OpenMDM <a class="header-anchor" href="#_3-openmdm" aria-label="Permalink to &quot;3. OpenMDM&quot;">​</a></h3><ul><li><strong>官網</strong>：<a href="https://github.com/openmdm" target="_blank" rel="noreferrer">https://github.com/openmdm</a></li><li><strong>授權</strong>：開源</li><li><strong>類型</strong>：基礎 MDM 框架</li></ul><h3 id="_4-hivemdm" tabindex="-1">4. HiveMDM <a class="header-anchor" href="#_4-hivemdm" aria-label="Permalink to &quot;4. HiveMDM&quot;">​</a></h3><ul><li><strong>授權</strong>：開源</li><li><strong>類型</strong>：技術型 MDM 方案</li></ul><h3 id="_5-piemdm" tabindex="-1">5. PieMDM <a class="header-anchor" href="#_5-piemdm" aria-label="Permalink to &quot;5. PieMDM&quot;">​</a></h3><ul><li><strong>官網 / 項目地址</strong>：<a href="https://github.com/pieteams/piemdm" target="_blank" rel="noreferrer">https://github.com/pieteams/piemdm</a></li><li><strong>項目網站</strong>：<a href="https://pieteams.github.io/piemdm/" target="_blank" rel="noreferrer">https://pieteams.github.io/piemdm/</a></li><li><strong>授權</strong>：MIT 開源許可</li><li><strong>類型</strong>：開源企業級 MDM 系統</li><li><strong>技術棧</strong>：Go 後端 + Vue 前端</li><li><strong>主要功能</strong>： <ul><li>主數據建模與數據管理</li><li>數據治理與訪問控制</li><li>工作流 / 審批管理</li><li>系統集成 &amp; API 支持</li></ul></li><li><strong>適用場景</strong>： <ul><li>適合嘗試性、PoC 或希望基於源碼擴展的主數據項目</li><li>社區實踐、自主研發型企業</li></ul></li></ul><h2 id="四、按維度快速索引" tabindex="-1">四、按維度快速索引 <a class="header-anchor" href="#四、按維度快速索引" aria-label="Permalink to &quot;四、按維度快速索引&quot;">​</a></h2><h3 id="商業-vs-開源" tabindex="-1">商業 vs 開源 <a class="header-anchor" href="#商業-vs-開源" aria-label="Permalink to &quot;商業 vs 開源&quot;">​</a></h3><ul><li><strong>商業</strong>：Informatica / SAP / Oracle / Stibo / 用友 / 普元</li><li><strong>開源</strong>：Pimcore / AtroCore / OpenMDM / PieMDM</li></ul><h3 id="國際-vs-中國" tabindex="-1">國際 vs 中國 <a class="header-anchor" href="#國際-vs-中國" aria-label="Permalink to &quot;國際 vs 中國&quot;">​</a></h3><ul><li><strong>國際</strong>：Informatica / SAP / Oracle / Stibo / Reltio</li><li><strong>中國</strong>：用友 / 金蝶 / 普元 / 英諾森 / 漢得 / PieMDM</li></ul><h2 id="五、選型建議-簡要" tabindex="-1">五、選型建議（簡要） <a class="header-anchor" href="#五、選型建議-簡要" aria-label="Permalink to &quot;五、選型建議（簡要）&quot;">​</a></h2><ul><li><strong>全球集團 / 複雜治理</strong>：Informatica / SAP MDG</li><li><strong>製造業主數據</strong>：SAP MDG / 英諾森</li><li><strong>國產化要求</strong>：用友 / 普元 / 金蝶 / PieMDM</li><li><strong>低成本探索 / 原型驗證</strong>：Pimcore / AtroCore / PieMDM</li></ul>',54)])])}const u=t(o,[["render",i]]);export{c as __pageData,u as default};
